import numpy
import math
import random

def strategy(threshold):
    throw1 = random.uniform(0,1)
    if throw1 >= threshold:
        return throw1
    else:
        return random.uniform(0,1)

def simulate_x_throws(x, func, threshold):
    data = numpy.zeros(x)
    for i in range(x):
        data[i] = func(threshold)
    return data

